mapper.select:
		select `username`,`password`,`dept_id`,`status`,`user_id_create`,`gmt_create`,
		`gmt_modified`,`user_type`,`register_type` from sys_user

		  		   and username = #{username} 
		  		   and password = #{password} 
		  		   and dept_id = #{deptId} 
		  		   and status = #{status} 
		  		   and user_id_create = #{userIdCreate} 
		  		   and gmt_create = #{gmtCreate} 
		  		   and gmt_modified = #{gmtModified} 
				   and user_type = #{userType} 
				   and register_type = #{registerType} 



                order by ${sort} ${order}


                order by user_id desc



			limit #{offset}, #{limit}

select `username`,`password`,`dept_id`,`status`,`user_id_create`,`gmt_create`,
`gmt_modified`,`user_type`,`register_type` from sys_user

	 and username = #{username}
	 and password = #{password} 
	 and dept_id = #{deptId} 
	 and status = #{status} 
	 and user_id_create = #{userIdCreate} 
	 and gmt_create = #{gmtCreate} 
	 and gmt_modified = #{gmtModified} 
	 and user_type = #{userType} 
	 and register_type = #{registerType}

SELECT s.* FROM sys_user s INNER JOIN ( SELECT dept_id FROM ( SELECT t1.dept_id,
IF ( find_in_set(parent_id, @pids) > 0, @pids := concat(@pids, ',', dept_id), 0 ) AS ischild FROM ( SELECT dept_id, parent_id FROM sys_dept t WHERE t.del_flag = 0 ORDER BY dept_id, parent_id, order_num ) t1, (SELECT @pids := #{deptId}) t2 ) t3 WHERE ischild = #{deptId} ) AS dlist ON dlist.dept_id = s.dept_id
limit #{offset}, #{limit}

select count(*) from sys_user

  		   and username = #{username} 
  		   and password = #{password} 
  		   and dept_id = #{deptId} 
  		   and status = #{status} 
  		   and user_id_create = #{userIdCreate} 
  		   and gmt_create = #{gmtCreate} 
  		   and gmt_modified = #{gmtModified} 
		   and user_type = #{userType} 
		   and register_type = #{registerType}
